mapper.insert:
INSERT INTO customer
SET name = #{name}
    , address = #{address}
    , phone = #{phone}

mapper.select:
SELECT custid AS custId, name, address, phone
FROM customer


        WHERE ${searchType} LIKE '%${searchText}%'
        ORDER BY ${searchType}


        ORDER BY custid


LIMIT #{startIdx}, #{size}

mapper.update:
UPDATE customer


        name = #{name}


       , address = #{address}


       , phone = #{phone}


WHERE custid = #{custId}
